import React, { useEffect, useRef } from 'react';

const PixelBackground = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let width, height;
        let animationFrameId;
        let particles = [];

        // Configuration
        const pixelSize = 25; // Size of each pixel
        const gap = 2; // Gap between pixels
        const baseColor = { r: 20, g: 20, b: 40 }; // Dark background pixel
        const activeColor = { r: 0, g: 255, b: 255 }; // Cyan for interaction
        const secondaryColor = { r: 139, g: 0, b: 255 }; // Purple for wave

        let mouse = { x: -1000, y: -1000 };

        const init = () => {
            width = canvas.width = window.innerWidth;
            height = canvas.height = window.innerHeight;
            particles = [];

            const cols = Math.ceil(width / (pixelSize + gap));
            const rows = Math.ceil(height / (pixelSize + gap));

            for (let i = 0; i < cols; i++) {
                for (let j = 0; j < rows; j++) {
                    particles.push({
                        x: i * (pixelSize + gap),
                        y: j * (pixelSize + gap),
                        originX: i * (pixelSize + gap),
                        originY: j * (pixelSize + gap),
                        color: baseColor,
                        size: pixelSize
                    });
                }
            }
        };

        const draw = (time) => {
            ctx.fillStyle = '#050505';
            ctx.fillRect(0, 0, width, height);

            particles.forEach(p => {
                // Wave calculation
                const dist = Math.sqrt((p.x - mouse.x) ** 2 + (p.y - mouse.y) ** 2);
                const maxDist = 250;

                // Ambient wave
                const wave = Math.sin(p.x * 0.01 + time * 0.002) * Math.cos(p.y * 0.01 + time * 0.002);

                let r = baseColor.r;
                let g = baseColor.g;
                let b = baseColor.b;
                let alpha = 0.3 + wave * 0.1;

                // Mouse interaction
                if (dist < maxDist) {
                    const force = (maxDist - dist) / maxDist;
                    r = activeColor.r * force + baseColor.r * (1 - force);
                    g = activeColor.g * force + baseColor.g * (1 - force);
                    b = activeColor.b * force + baseColor.b * (1 - force);
                    alpha = 0.8 * force + 0.2;
                } else if (wave > 0.8) {
                    // Occasional purple highlights from the wave
                    r = secondaryColor.r;
                    g = secondaryColor.g;
                    b = secondaryColor.b;
                    alpha = 0.5 * wave;
                }

                ctx.fillStyle = `rgba(${r}, ${g}, ${b}, ${alpha})`;
                ctx.fillRect(p.x, p.y, pixelSize, pixelSize);
            });
        };

        const animate = (time) => {
            draw(time);
            animationFrameId = requestAnimationFrame(animate);
        };

        const handleResize = () => {
            init();
        };

        const handleMouseMove = (e) => {
            const rect = canvas.getBoundingClientRect();
            mouse.x = e.clientX - rect.left;
            mouse.y = e.clientY - rect.top;
        };

        window.addEventListener('resize', handleResize);
        window.addEventListener('mousemove', handleMouseMove);

        init();
        animate(0);

        return () => {
            window.removeEventListener('resize', handleResize);
            window.removeEventListener('mousemove', handleMouseMove);
            cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                zIndex: 0,
                pointerEvents: 'none' // Let clicks pass through
            }}
        />
    );
};

export default PixelBackground;
