import React, { useEffect, useRef } from 'react';

const PixelBackground = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let width, height;
        let animationFrameId;

        // Settings
        let particles = [];
        let hue = 220;

        let mouse = { x: -1000, y: -1000 };
        let mouseVel = { x: 0, y: 0 };

        class NebulaParticle {
            constructor() {
                this.x = Math.random() * width;
                this.y = Math.random() * height;
                // Faster movement
                this.vx = (Math.random() - 0.5) * 1.5;
                this.vy = (Math.random() - 0.5) * 1.5;

                this.baseRadius = 250 + Math.random() * 300;
                this.radius = this.baseRadius;

                // Morphing parameters
                this.morphSpeed = 0.02 + Math.random() * 0.03;
                this.morphOffset = Math.random() * 100;

                this.baseAlpha = 0.3 + Math.random() * 0.3; // More opaque
                this.hueOffset = Math.random() * 80 - 40;
            }

            update(time) {
                // 1. Organic Wandering (Perlin-ish)
                // Change direction slowly over time
                const angle = time * 0.0005 + this.morphOffset;
                this.vx += Math.sin(angle) * 0.02;
                this.vy += Math.cos(angle) * 0.02;

                // Speed limit
                const speed = Math.sqrt(this.vx * this.vx + this.vy * this.vy);
                if (speed > 2) {
                    this.vx = (this.vx / speed) * 2;
                    this.vy = (this.vy / speed) * 2;
                }

                this.x += this.vx;
                this.y += this.vy;

                // 2. Morphing (Pulse size)
                // Sine wave breathing
                this.radius = this.baseRadius + Math.sin(time * this.morphSpeed + this.morphOffset) * 50;

                // 3. Mouse Turbulence
                const dx = this.x - mouse.x;
                const dy = this.y - mouse.y;
                const dist = Math.sqrt(dx * dx + dy * dy);
                const interactRadius = 500;

                if (dist < interactRadius) {
                    const force = (interactRadius - dist) / interactRadius;
                    this.vx += mouseVel.x * force * 0.02;
                    this.vy += mouseVel.y * force * 0.02;
                }

                // 4. Wrap Around (Better distribution)
                // Use a buffer zone to ensure they enter fully before warping
                const buffer = 400;
                if (this.x < -buffer) this.x = width + buffer;
                if (this.x > width + buffer) this.x = -buffer;
                if (this.y < -buffer) this.y = height + buffer;
                if (this.y > height + buffer) this.y = -buffer;
            }

            draw(ctx) {
                // Sharper contours using different gradient stops
                ctx.globalCompositeOperation = 'hard-light'; // Stronger blending

                const gradient = ctx.createRadialGradient(this.x, this.y, 0, this.x, this.y, this.radius);
                const currentHue = (hue + this.hueOffset) % 360;

                // Solid core, sharper falloff
                gradient.addColorStop(0, `hsla(${currentHue}, 80%, 60%, ${this.baseAlpha})`);
                gradient.addColorStop(0.4, `hsla(${currentHue}, 70%, 50%, ${this.baseAlpha * 0.6})`);
                gradient.addColorStop(1, `hsla(${currentHue}, 70%, 10%, 0)`);

                ctx.fillStyle = gradient;
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
                ctx.fill();

                ctx.globalCompositeOperation = 'source-over';
            }
        }

        const init = () => {
            width = canvas.width = window.innerWidth;
            height = canvas.height = window.innerHeight;

            particles = [];
            // More particles for better coverage
            for (let i = 0; i < 40; i++) {
                particles.push(new NebulaParticle());
            }
        };

        const draw = (time) => {
            ctx.fillStyle = '#050510';
            ctx.fillRect(0, 0, width, height);

            hue = (hue + 0.5) % 360; // Fast color cycle

            mouseVel.x *= 0.95;
            mouseVel.y *= 0.95;

            particles.forEach(p => {
                p.update(time);
                p.draw(ctx);
            });
        };

        const animate = (time) => {
            draw(time);
            animationFrameId = requestAnimationFrame(animate);
        };

        const handleResize = () => {
            init();
        };

        const handleMouseMove = (e) => {
            const rect = canvas.getBoundingClientRect();
            const currentX = e.clientX - rect.left;
            const currentY = e.clientY - rect.top;

            mouseVel.x = currentX - mouse.x;
            mouseVel.y = currentY - mouse.y;
            mouse.x = currentX;
            mouse.y = currentY;
        };

        window.addEventListener('resize', handleResize);
        window.addEventListener('mousemove', handleMouseMove);

        init();
        animate(0);

        return () => {
            window.removeEventListener('resize', handleResize);
            window.removeEventListener('mousemove', handleMouseMove);
            cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                zIndex: 0,
                pointerEvents: 'none'
            }}
        />
    );
};

export default PixelBackground;
