import React from 'react';
import { Link, useLocation } from 'react-router-dom';

export default function PortfolioHeader() {
    const location = useLocation();
    const isHome = location.pathname === '/';

    return (
        <header className="portfolio-header" style={isHome ? { background: 'transparent', borderBottomColor: 'transparent' } : {}}>
            <Link to="/" className="portfolio-header__logo">
                Hishikawa Yuki
            </Link>
            <nav className="portfolio-header__nav">
                <Link to="/deploy-projects">Deploy Projects</Link>
                <Link to="/contact">Contact</Link>
            </nav>
        </header>
    );
}
