import { useState, useEffect } from "react";
import liff from "@line/liff"; 
import "./styles.css";

// ÂÆöÊï∞„ÅÆÁÆ±
const COMMON_OPTIONS = [
  { text: "ÈùûÂ∏∏„Å´„Åù„ÅÜÊÄù„ÅÜ ", score: 4 },
  { text: "„Åù„ÅÜÊÄù„ÅÜ ", score: 3 },
  { text: "„Åù„ÅÜÊÄù„Çè„Å™„ÅÑ ", score: 2 },
  { text: "ÂÖ®„Åè„Åù„ÅÜÊÄù„Çè„Å™„ÅÑ ", score: 1 },
];

const questions = [
  { id: 1, text: "‰ºëÊó•„ÅØÂÆ∂„Åß„ÇÜ„Å£„Åè„Çä„Åô„Çã„Çà„Çä„ÇÇ„ÄÅÂ§ñ„Å´Âá∫„Åã„Åë„Åü„ÅÑÊ¥æ„Å† " },
  { id: 2, text: "ÂàùÂØæÈù¢„ÅÆ‰∫∫„Å®„ÇÇÁ∑äÂºµ„Åõ„Åö„Å´Ë©±„Åõ„ÇãÊñπ„Å† " },
  { id: 3, text: "„É¨„Çπ„Éà„É©„É≥„ÅÆ„É°„Éã„É•„Éº„ÅØÁõ¥ÊÑü„ÅßÂç≥Ê±∫„Åô„Çã " },
  { id: 4, text: "Ë®àÁîª„ÇíÁ´ã„Å¶„Å¶Ë°åÂãï„Åô„Çã„Çà„Çä„ÄÅ„Åù„ÅÆÂ†¥„ÅÆ„Éé„É™„ÅåÂ§ß‰∫ã„Å† " },
  { id: 5, text: "‰∏Ä‰∫∫„Åß‰ΩúÊ•≠„Åô„Çã„Çà„Çä„ÄÅ„ÉÅ„Éº„É†„Åß„ÉØ„Ç§„ÉØ„Ç§„ÇÑ„Çã„ÅÆ„ÅåÂ•Ω„Åç„Å† " },
  { id: 6, text: "Êñ∞„Åó„ÅÑ„Ç¨„Ç∏„Çß„ÉÉ„Éà„ÇÑÊµÅË°å„Çä„ÅÆÂ∫ó„Å´„ÅØ„Åô„Åê„Å´È£õ„Å≥„Å§„Åè " },
  { id: 7, text: "ÊÑüÊÉÖ„ÅåÈ°î„Å´Âá∫„ÇÑ„Åô„ÅÑ„Å®Ë®Ä„Çè„Çå„Çã„Åì„Å®„Åå„ÅÇ„Çã " },
  { id: 8, text: "„É™„Çπ„ÇØ„Åå„ÅÇ„Å£„Å¶„ÇÇ„ÄÅ„É™„Çø„Éº„É≥„ÅåÂ§ß„Åç„ÅÑÂãùË≤†„ÇíÈÅ∏„Å≥„Åü„ÅÑ " },
  { id: 9, text: "ÈÅéÂéª„ÅÆÂ§±Êïó„Çí„ÅÑ„Å§„Åæ„Åß„ÇÇÂºï„Åç„Åö„Çâ„Å™„ÅÑÊñπ„Å† " },
];

const results = [
  { type: "A", title: "ÁπîÁî∞‰ø°Èï∑„Åß„Åó„Åüüåà", image: "/image/type-a.png", minScore: 0, maxScore: 4 },
  { type: "B", title: "ÂùÇÊú¨ÈæçÈ¶¨„Åß„Åó„Åüüåà", image: "/image/type-b.png", minScore: 5, maxScore: 8 },
  { type: "C", title: "Ë´∏ËëõÂ≠îÊòé„Åß„Åó„Åüüåà", image: "/image/type-c.png", minScore: 9, maxScore: 12 },
  { type: "D", title: "Ë±äËá£ÁßÄÂêâ„Åß„Åó„Åüüåà", image: "/image/type-d.png", minScore: 13, maxScore: 16 },
  { type: "E", title: "„ÉÄ„Éª„É¥„Ç£„É≥„ÉÅ„Åß„Åó„Åüüåà", image: "/image/type-e.png", minScore: 17, maxScore: 20 },
  { type: "F", title: "„Éä„Ç§„ÉÅ„É≥„Ç≤„Éº„É´„Åß„Åó„Åüüåà", image: "/image/type-n.png", minScore: 21, maxScore: 24 },
  { type: "G", title: "„Ç®„Ç∏„ÇΩ„É≥„Åß„Åó„Åüüåà", image: "/image/type-g.png", minScore: 25, maxScore: 28 },
  { type: "H", title: "„Éû„É™„Éº„Éª„Ç¢„É≥„Éà„ÉØ„Éç„ÉÉ„Éà„Åß„Åó„Åüüåà", image: "/image/type-h.png", minScore: 29, maxScore: 999 },
];
// ‰∏≠Ë∫´„ÅåÊõ∏„ÅçÊèõ„Çè„ÇãÁÆ±

export default function Diagnosis() {
  const [isStarted, setIsStarted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [finishedResult, setFinishedResult] = useState(null);
  const [userName, setUserName] = useState("„Ç≤„Çπ„Éà");

  useEffect(() => {
    liff
      .init({ liffId: "2009029412-d1KRC9Mp" })
      .then(() => {
        if (liff.isLoggedIn()) {
          liff.getProfile().then((profile) => {
            setUserName(profile.displayName);
          }).catch((err) => console.error(err));
        }
      })
      .catch((err) => console.error(err));
  }, []);

  const handleStart = () => setIsStarted(true);
  const handleRestart = () => {
    setIsStarted(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setFinishedResult(null);
  };
  const handleClickAnswer = (optionScore) => {
    const newScore = score + optionScore;
    setScore(newScore);
    const nextQuestion = currentQuestionIndex + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      const result = results.find((r) => newScore >= r.minScore && newScore <= r.maxScore);
      setFinishedResult(result);
    }
  };

  return (
    <div className="diagnosis-app-wrapper">
      
      <div className="diagnosis-container">
        
        <div className="App">
          {finishedResult ? (
            /* ÁµêÊûúÁîªÈù¢ */
            <div className="result-screen">
              <h2>{userName}„Åï„Çì„ÅØ<br />{finishedResult.title}</h2>
              <img src={finishedResult.image} alt={finishedResult.title} className="result-image" />
              <div style={{ marginTop: "40px", borderTop: "1px solid #eee", paddingTop: "20px", width: "100%" }}>
                <button onClick={handleRestart} className="restart-btn">ÊúÄÂàù„Åã„ÇâË®∫Êñ≠„Åó„Å™„Åä„ÅôüëÄ</button>
              </div>
            </div>
          ) : !isStarted ? (
            /* „Çπ„Çø„Éº„ÉàÁîªÈù¢ */
            <div className="start-screen">
              <div className="line-icon">üëª</div>
              <div className="speech-bubble">
                <p>„Åì„Çì„Å´„Å°„ÅØÔºÅ{userName}„Åï„Çì‚ú®</p>
                <p>„ÅÇ„Å™„Åü„ÅÆÊÄßÊ†º„Çí9„Å§„ÅÆË≥™Âïè„ÅßË®∫Êñ≠„Åó„Åæ„Åôüí°</p>
                <p>ÊÇ©„Åæ„Åö„Å´Áõ¥Ë¶≥ÁöÑ„Å´ÈÅ∏„Çì„Åß„Åè„Å†„Åï„ÅÑÔºÅüëá</p>
              </div>
              <img src="/image/start.png" alt="„Çπ„Çø„Éº„Éà" className="main-image" />
              <button onClick={handleStart} className="start-btn">Ë®∫Êñ≠„Çí„Çπ„Çø„Éº„Éà„Åô„Çã üöÄ</button>
            </div>
          ) : (
            /* Ë≥™ÂïèÁîªÈù¢ */
            <div className="question-screen" key={currentQuestionIndex}>
              <div className="line-icon">üëª</div>
              <div className="speech-bubble">
                <div className="progress-text">Q.{questions[currentQuestionIndex].id} / {questions.length}</div>
                <h2 className="question-text">{questions[currentQuestionIndex].text}</h2>
              </div>
              <div className="button-container">
                {COMMON_OPTIONS.map((option) => (
                  <button key={option.text} onClick={() => handleClickAnswer(option.score)} className="diagnosis-option-btn">
                    {option.text}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
        
      </div>
    </div>
  );
}
