import React, { useEffect, useRef, useState } from 'react';

const CHARS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';

/**
 * ShuffleText
 * Animates the text by randomizing characters, then resolving them one by one.
 * @param {string} text  — Final text to display
 * @param {number} duration — Total animation duration in ms (default 1200)
 * @param {number} delay — Delay before starting in ms (default 0)
 * @param {string} className — CSS class to apply on the span
 */
export default function ShuffleText({ text, duration = 1200, delay = 0, className = '' }) {
    const [display, setDisplay] = useState(text);
    const rafRef = useRef(null);

    useEffect(() => {
        let start = null;
        let timeout;

        const step = (timestamp) => {
            if (!start) start = timestamp;
            const elapsed = timestamp - start;
            const progress = Math.min(elapsed / duration, 1);

            // How many characters have been "resolved" (left to right)
            const resolved = Math.floor(progress * text.length);

            const chars = text
                .split('')
                .map((char, i) => {
                    if (char === ' ') return ' ';
                    if (i < resolved) return char;
                    return CHARS[Math.floor(Math.random() * CHARS.length)];
                })
                .join('');

            setDisplay(chars);

            if (progress < 1) {
                rafRef.current = requestAnimationFrame(step);
            } else {
                setDisplay(text);
            }
        };

        timeout = setTimeout(() => {
            rafRef.current = requestAnimationFrame(step);
        }, delay);

        return () => {
            clearTimeout(timeout);
            if (rafRef.current) cancelAnimationFrame(rafRef.current);
        };
        // re-run only on mount (each page load)
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    return <span className={className}>{display}</span>;
}
