import React, { useEffect, useRef } from 'react';

const PixelBackground = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let width, height;
        let animationFrameId;

        let particles = [];
        let hue = 220; // Start with blue/purple/cyan range

        let mouse = { x: -1000, y: -1000 };
        // Track mouse velocity for turbulence
        let lastMouse = { x: -1000, y: -1000 };
        let mouseVel = { x: 0, y: 0 };

        // Nebula Particle Class
        class NebulaParticle {
            constructor() {
                this.x = Math.random() * width;
                this.y = Math.random() * height;
                this.vx = (Math.random() - 0.5) * 0.2; // Very slow drift
                this.vy = (Math.random() - 0.5) * 0.2;
                this.radius = 200 + Math.random() * 300; // Large soft particles
                this.baseAlpha = 0.1 + Math.random() * 0.2;
                this.hueOffset = Math.random() * 60 - 30;
                this.life = Math.random() * 1000;
            }

            update(time) {
                // 1. Natural Drift
                this.x += this.vx;
                this.y += this.vy;

                // 2. Mouse Turbulence (Swirl)
                const dx = this.x - mouse.x;
                const dy = this.y - mouse.y;
                const dist = Math.sqrt(dx * dx + dy * dy);
                const interactRadius = 400;

                if (dist < interactRadius) {
                    const force = (interactRadius - dist) / interactRadius;
                    // Push slightly with mouse velocity
                    this.vx += mouseVel.x * force * 0.05;
                    this.vy += mouseVel.y * force * 0.05;
                }

                // Damping
                this.vx *= 0.98;
                this.vy *= 0.98;

                // Keep continuous slightly
                if (Math.abs(this.vx) < 0.1) this.vx += (Math.random() - 0.5) * 0.01;
                if (Math.abs(this.vy) < 0.1) this.vy += (Math.random() - 0.5) * 0.01;


                // Wrap around screen
                if (this.x < -this.radius) this.x = width + this.radius;
                if (this.x > width + this.radius) this.x = -this.radius;
                if (this.y < -this.radius) this.y = height + this.radius;
                if (this.y > height + this.radius) this.y = -this.radius;
            }

            draw(ctx) {
                // Create gradient for soft fog look
                // Use screen blending for glowing overlay effect
                ctx.globalCompositeOperation = 'screen';

                const gradient = ctx.createRadialGradient(this.x, this.y, 0, this.x, this.y, this.radius);
                const currentHue = (hue + this.hueOffset) % 360;

                gradient.addColorStop(0, `hsla(${currentHue}, 70%, 50%, ${this.baseAlpha})`);
                gradient.addColorStop(1, `hsla(${currentHue}, 70%, 10%, 0)`);

                ctx.fillStyle = gradient;
                ctx.beginPath();
                ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
                ctx.fill();

                ctx.globalCompositeOperation = 'source-over'; // Reset
            }
        }

        const init = () => {
            width = canvas.width = window.innerWidth;
            height = canvas.height = window.innerHeight;

            particles = [];
            // Create fewer, larger particles for massive fog effect
            for (let i = 0; i < 30; i++) {
                particles.push(new NebulaParticle());
            }
        };

        const draw = (time) => {
            // Dark deep space background
            ctx.fillStyle = '#050510';
            ctx.fillRect(0, 0, width, height);

            // Update global hue
            hue = (hue + 0.05) % 360;

            // Reduce mouse velocity
            mouseVel.x *= 0.9;
            mouseVel.y *= 0.9;

            particles.forEach(p => {
                p.update(time);
                p.draw(ctx);
            });
        };

        const animate = (time) => {
            draw(time);
            animationFrameId = requestAnimationFrame(animate);
        };

        const handleResize = () => {
            init();
        };

        const handleMouseMove = (e) => {
            const rect = canvas.getBoundingClientRect();
            const currentX = e.clientX - rect.left;
            const currentY = e.clientY - rect.top;

            // Calculate velocity
            mouseVel.x = currentX - mouse.x;
            mouseVel.y = currentY - mouse.y;

            mouse.x = currentX;
            mouse.y = currentY;
        };

        window.addEventListener('resize', handleResize);
        window.addEventListener('mousemove', handleMouseMove);

        init();
        animate(0);

        return () => {
            window.removeEventListener('resize', handleResize);
            window.removeEventListener('mousemove', handleMouseMove);
            cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                zIndex: 0,
                pointerEvents: 'none'
            }}
        />
    );
};

export default PixelBackground;
